using System;
using System.Threading;

namespace Ex1
{
    class ThreadExample
    {
        static void Main()
        {
            Thread t = new Thread(Write1);
            t.Start();

            for (int i = 0; i < 1000; i++)
            {
                Console.Write("0");
            }
        }

        // Inny wątek
        static void Write1()
        {
            for (int i = 0; i < 1000; i++)
            {
                Console.Write("1");
            }
        }
    }
}

